import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  const features = [
    {
      title: "Profesionales Certificados",
      description: "Nuestro equipo cuenta con especialistas certificados en neuropsicología con amplia experiencia clínica."
    },
    {
      title: "Métodos Basados en Evidencia Científica", 
      description: "Utilizamos técnicas y protocolos respaldados por la investigación científica más actualizada."
    },
    {
      title: "Atención Personalizada",
      description: "Cada tratamiento es diseñado específicamente para las necesidades individuales de cada paciente."
    },
    {
      title: "Seguimiento Presencial y En Línea",
      description: "Ofrecemos flexibilidad en el seguimiento con opciones presenciales y telemedicina."
    }
  ];

  const images = [
    {
      src: "/api/placeholder/300/200",
      alt: "Consulta profesional",
      className: "rounded-2xl shadow-lg"
    },
    {
      src: "/api/placeholder/280/180", 
      alt: "Terapia grupal",
      className: "rounded-2xl shadow-lg"
    },
    {
      src: "/api/placeholder/320/220",
      alt: "Evaluación neuropsicológica",
      className: "rounded-2xl shadow-lg"
    },
    {
      src: "/api/placeholder/290/190",
      alt: "Sesión de rehabilitación",
      className: "rounded-2xl shadow-lg"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 0.6
      }
    }
  };

  return (
    <section id="nosotros" className="py-20 bg-gradient-to-br from-gray-50 to-neurops-blue/5">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="text-3xl font-bold text-neurops-black sm:text-4xl lg:text-5xl">
            ¿Por qué elegir <span className="text-neurops-black">Neurops</span>?
          </h2>
          <p className="mt-4 max-w-2xl mx-auto text-xl text-gray-600">
            Nos distinguimos por nuestro compromiso con la excelencia y la atención integral
          </p>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-12 items-center">
          {/* Imagen/Ilustración */}
          <motion.div
            className="relative"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="relative bg-gradient-to-br from-neurops-royal/10 to-neurops-blue/10 rounded-3xl p-8 h-96 flex items-center justify-center">
              {/* Ilustración de equipo médico */}
              <motion.div
                className="relative"
                animate={{ 
                  y: [0, -10, 0],
                }}
                transition={{ 
                  duration: 4,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              >
                <svg
                  width="280"
                  height="280"
                  viewBox="0 0 280 280"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  {/* Personas representando el equipo */}
                  <circle cx="100" cy="120" r="25" fill="#7EAAD7" className="opacity-80"/>
                  <rect x="75" y="145" width="50" height="60" rx="25" fill="#79B58C" className="opacity-80"/>
                  
                  <circle cx="180" cy="120" r="25" fill="#6E7DB7" className="opacity-80"/>
                  <rect x="155" y="145" width="50" height="60" rx="25" fill="#D67D2B" className="opacity-80"/>
                  
                  <circle cx="140" cy="80" r="30" fill="#F7DD54" className="opacity-80"/>
                  <rect x="110" y="110" width="60" height="70" rx="30" fill="#CF634C" className="opacity-80"/>
                  
                  {/* Elementos decorativos */}
                  <motion.circle
                    cx="60"
                    cy="80"
                    r="8"
                    fill="#79B58C"
                    animate={{ scale: [1, 1.2, 1] }}
                    transition={{ duration: 2, repeat: Infinity, delay: 0.5 }}
                  />
                  <motion.circle
                    cx="220"
                    cy="100"
                    r="6"
                    fill="#F7DD54"
                    animate={{ scale: [1, 1.3, 1] }}
                    transition={{ duration: 2.5, repeat: Infinity, delay: 1 }}
                  />
                  <motion.circle
                    cx="200"
                    cy="200"
                    r="10"
                    fill="#7EAAD7"
                    animate={{ scale: [1, 1.1, 1] }}
                    transition={{ duration: 3, repeat: Infinity, delay: 1.5 }}
                  />
                </svg>
              </motion.div>

              {/* Elementos flotantes */}
              <motion.div
                className="absolute top-8 right-8 w-4 h-4 bg-neurops-yellow rounded-full"
                animate={{ 
                  rotate: 360,
                  scale: [1, 1.2, 1]
                }}
                transition={{ duration: 8, repeat: Infinity }}
              />
              <motion.div
                className="absolute bottom-12 left-8 w-6 h-6 bg-neurops-royal rounded-full"
                animate={{ 
                  y: [0, -15, 0],
                  opacity: [0.7, 1, 0.7]
                }}
                transition={{ duration: 3, repeat: Infinity, delay: 0.5 }}
              />
            </div>
          </motion.div>

          {/* Features */}
          <motion.div
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            className="space-y-8"
          >
            {features.map((feature, index) => (
              <motion.div
                key={index}
                variants={itemVariants}
                className="flex items-start space-x-4"
              >
                <motion.div
                  className={`flex-shrink-0 w-12 h-12 ${feature.color} rounded-xl flex items-center justify-center text-white shadow-lg`}
                  whileHover={{ scale: 1.1, rotate: 5 }}
                  transition={{ duration: 0.3 }}
                >
                  {feature.icon}
                </motion.div>
                <div className="flex-1">
                  <h3 className="text-xl font-bold text-neurops-black mb-2">
                    {feature.title}
                  </h3>
                  <p className="text-gray-600 leading-relaxed">
                    {feature.description}
                  </p>
                </div>
              </motion.div>
            ))}
          </motion.div>
        </div>

        {/* Estadísticas */}
        <motion.div
          className="mt-20 grid grid-cols-2 md:grid-cols-4 gap-8"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}
          viewport={{ once: true }}
        >
          {[
            { number: "500+", label: "Pacientes Atendidos" },
            { number: "15+", label: "Años de Experiencia" },
            { number: "95%", label: "Satisfacción del Cliente" },
            { number: "24/7", label: "Soporte Disponible" }
          ].map((stat, index) => (
            <motion.div
              key={index}
              className="text-center"
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.3 }}
            >
              <motion.div
                className="text-3xl md:text-4xl font-bold text-neurops-black mb-2"
                initial={{ scale: 0 }}
                whileInView={{ scale: 1 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                {stat.number}
              </motion.div>
              <div className="text-sm text-gray-600 font-medium">
                {stat.label}
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default About;
