import React from 'react';
import { motion } from 'framer-motion';

const Testimonials = () => {
  const testimonials = [
    {
      quote: "Gracias a Neurops mejoré mis habilidades cognitivas y volví a confiar en mí misma. El tratamiento personalizado fue exactamente lo que necesitaba.",
      name: "María González",
      role: "Paciente - Rehabilitación Cognitiva",
      avatar: "MG",
      color: "bg-neurops-green"
    },
    {
      quote: "El equipo de profesionales es excepcional. Mi hijo ha mostrado mejoras significativas en su atención y rendimiento académico.",
      name: "Carlos Rodríguez",
      role: "Padre de familia",
      avatar: "CR",
      color: "bg-neurops-blue"
    },
    {
      quote: "La evaluación neuropsicológica me ayudó a entender mejor mis fortalezas y áreas de mejora. Ahora tengo herramientas concretas para mi desarrollo profesional.",
      name: "Ana Martínez",
      role: "Profesional - Asesoría Laboral",
      avatar: "AM",
      color: "bg-neurops-royal"
    },
    {
      quote: "Después del accidente, pensé que no podría recuperar mis capacidades. Neurops me demostró que sí era posible. Eternamente agradecida.",
      name: "Luis Fernández",
      role: "Paciente - Rehabilitación Neuronal",
      avatar: "LF",
      color: "bg-neurops-orange"
    },
    {
      quote: "La atención en línea fue perfecta durante la pandemia. Nunca perdí continuidad en mi tratamiento y los resultados fueron excelentes.",
      name: "Patricia Silva",
      role: "Paciente - Terapia Cognitiva",
      avatar: "PS",
      color: "bg-neurops-yellow text-neurops-black"
    },
    {
      quote: "Como profesional de la salud, puedo confirmar que Neurops maneja los más altos estándares de calidad y evidencia científica.",
      name: "Dr. Roberto Díaz",
      role: "Médico Neurólogo",
      avatar: "RD",
      color: "bg-neurops-red"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6
      }
    }
  };

  return (
    <section id="pacientes" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="text-3xl font-bold text-neurops-black sm:text-4xl lg:text-5xl">
            Lo que dicen nuestros <span className="text-neurops-black">pacientes</span>
          </h2>
          <p className="mt-4 max-w-2xl mx-auto text-xl text-gray-600">
            Testimonios reales de personas que han transformado su vida con nuestros tratamientos
          </p>
        </motion.div>

        <motion.div
          className="grid gap-8 md:grid-cols-2 lg:grid-cols-3"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          {testimonials.map((testimonial, index) => (
            <motion.div
              key={index}
              variants={itemVariants}
              className="group"
            >
              <motion.div
                className="relative bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 p-8 h-full border border-gray-100"
                whileHover={{ y: -8, scale: 1.02 }}
                transition={{ duration: 0.3 }}
              >
                {/* Quote Icon */}
                <motion.div
                  className="absolute top-6 right-6 text-neurops-green/20"
                  initial={{ scale: 0, rotate: -180 }}
                  whileInView={{ scale: 1, rotate: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  viewport={{ once: true }}
                >
                  <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M14.017 21v-7.391c0-5.704 3.731-9.57 8.983-10.609l.995 2.151c-2.432.917-3.995 3.638-3.995 5.849h4v10h-9.983zm-14.017 0v-7.391c0-5.704 3.748-9.57 9-10.609l.996 2.151c-2.433.917-3.996 3.638-3.996 5.849h3.983v10h-9.983z"/>
                  </svg>
                </motion.div>

                {/* Testimonial Text */}
                <blockquote className="text-gray-700 italic leading-relaxed mb-6 relative z-10">
                  "{testimonial.quote}"
                </blockquote>

                {/* Author Info */}
                <div className="flex items-center space-x-4">
                  <motion.div
                    className={`w-12 h-12 ${testimonial.color} rounded-full flex items-center justify-center text-white font-bold text-sm shadow-lg`}
                    whileHover={{ scale: 1.1, rotate: 10 }}
                    transition={{ duration: 0.3 }}
                  >
                    {testimonial.avatar}
                  </motion.div>
                  <div>
                    <h4 className="font-bold text-neurops-black group-hover:text-gray-600 transition-colors duration-300">
                      {testimonial.name}
                    </h4>
                    <p className="text-sm text-gray-500">
                      {testimonial.role}
                    </p>
                  </div>
                </div>

                {/* Rating Stars */}
                <motion.div
                  className="flex space-x-1 mt-4"
                  initial={{ opacity: 0 }}
                  whileInView={{ opacity: 1 }}
                  transition={{ duration: 0.6, delay: index * 0.1 + 0.3 }}
                  viewport={{ once: true }}
                >
                  {[...Array(5)].map((_, i) => (
                    <motion.svg
                      key={i}
                      className="w-4 h-4 text-neurops-yellow"
                      fill="currentColor"
                      viewBox="0 0 20 20"
                      initial={{ scale: 0, rotate: -180 }}
                      whileInView={{ scale: 1, rotate: 0 }}
                      transition={{ duration: 0.3, delay: index * 0.1 + i * 0.1 }}
                      viewport={{ once: true }}
                    >
                      <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"/>
                    </motion.svg>
                  ))}
                </motion.div>

                {/* Hover effect background */}
                <div className="absolute inset-0 bg-gradient-to-br from-neurops-green/5 to-neurops-blue/5 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
              </motion.div>
            </motion.div>
          ))}
        </motion.div>

        {/* Trust indicators */}
        <motion.div
          className="mt-16 text-center"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}
          viewport={{ once: true }}
        >
          <div className="flex flex-wrap justify-center items-center space-x-8 space-y-4">
            <div className="flex items-center space-x-2 text-neurops-green">
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd"/>
              </svg>
              <span className="font-semibold">Certificados por el Colegio de Psicólogos</span>
            </div>
            <div className="flex items-center space-x-2 text-neurops-blue">
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
