import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

// Componente simple de carrusel de imágenes
const ImageCarousel = () => {
  const [currentImage, setCurrentImage] = useState(0);
  
  const images = [
    {
      bg: 'bg-gradient-to-b from-blue-50 to-blue-100',
      icon: (
        <svg className="w-16 h-16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
        </svg>
      ),
      title: 'Evaluación Neuropsicológica',
      subtitle: 'Diagnóstico especializado'
    },
    {
      bg: 'bg-gradient-to-b from-green-50 to-green-100',
      icon: (
        <svg className="w-16 h-16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
        </svg>
      ),
      title: 'Terapia Grupal',
      subtitle: 'Sesiones colaborativas'
    },
    {
      bg: 'bg-gradient-to-b from-purple-50 to-purple-100',
      icon: (
        <svg className="w-16 h-16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z" />
        </svg>
      ),
      title: 'Atención Personalizada',
      subtitle: 'Cuidado individual'
    },
    {
      bg: 'bg-gradient-to-b from-pink-50 to-pink-100',
      icon: (
        <svg className="w-16 h-16" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
        </svg>
      ),
      title: 'Profesionales Certificados',
      subtitle: 'Experiencia garantizada'
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [images.length]);

  const indicatorColors = [
    'bg-neurops-blue',   // Azul para la primera imagen
    'bg-neurops-green',  // Verde para la segunda imagen
    'bg-neurops-royal',  // Azul rey para la tercera imagen
    'bg-neurops-red'     // Rojo para la cuarta imagen
  ];

  return (
    <div className="w-80 h-full mx-auto rounded-3xl overflow-hidden relative">
      {images.map((image, index) => (
        <div
          key={index}
          className={`absolute inset-0 ${image.bg} flex flex-col items-center justify-center text-center transition-opacity duration-1000 ${
            index === currentImage ? 'opacity-100' : 'opacity-0'
          }`}
        >
          <div className="text-gray-500 mb-6">
            {image.icon}
          </div>
          <h3 className="text-3xl font-bold text-gray-700 mb-4 font-serif px-6">
            {image.title}
          </h3>
          <p className="text-gray-500 font-serif text-lg px-8">
            {image.subtitle}
          </p>
        </div>
      ))}
      
      {/* Indicadores con colores de marca */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {images.map((_, index) => (
          <div
            key={index}
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              index === currentImage 
                ? `${indicatorColors[index]} scale-110` 
                : 'bg-white border border-gray-300'
            }`}
          />
        ))}
      </div>
    </div>
  );
};

const About = () => {
  const features = [
    {
      title: "Profesionales Certificados",
      description: "Nuestro equipo cuenta con especialistas certificados en neuropsicología con amplia experiencia clínica."
    },
    {
      title: "Métodos Basados en Evidencia Científica", 
      description: "Utilizamos técnicas y protocolos respaldados por la investigación científica más actualizada."
    },
    {
      title: "Atención Personalizada",
      description: "Cada tratamiento es diseñado específicamente para las necesidades individuales de cada paciente."
    },
    {
      title: "Seguimiento Presencial y En Línea",
      description: "Ofrecemos flexibilidad en el seguimiento con opciones presenciales y telemedicina."
    }
  ];

  const images = [
    {
      src: "/api/placeholder/280/200",
      alt: "Consulta profesional",
      className: "rounded-3xl shadow-xl",
      size: "large",
      position: "top-left"
    },
    {
      src: "/api/placeholder/200/160", 
      alt: "Terapia grupal",
      className: "rounded-2xl shadow-lg",
      size: "small",
      position: "top-right"
    },
    {
      src: "/api/placeholder/220/180",
      alt: "Evaluación neuropsicológica",
      className: "rounded-2xl shadow-lg",
      size: "medium",
      position: "bottom-left"
    },
    {
      src: "/api/placeholder/260/220",
      alt: "Sesión de rehabilitación",
      className: "rounded-3xl shadow-xl",
      size: "large",
      position: "bottom-right"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 0.6
      }
    }
  };

  return (
    <section id="nosotros" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="text-3xl font-bold text-neurops-black sm:text-4xl lg:text-5xl">
            ¿Por qué elegir <span className="text-neurops-black">Neurops</span>?
          </h2>
          <p className="mt-4 max-w-2xl mx-auto text-xl text-gray-600">
            Nos distinguimos por nuestro compromiso con la excelencia y la atención integral
          </p>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-16 items-center">
          {/* Imagen vertical simple */}
          <motion.div
            className="relative w-full"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <ImageCarousel />
          </motion.div>

          {/* Lista de características sin iconos */}
          <motion.div
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            className="space-y-8"
          >
            {features.map((feature, index) => (
              <motion.div
                key={index}
                variants={itemVariants}
                className="group"
              >
                <div className="border-l-2 border-gray-200 pl-6 py-2 hover:border-neurops-black transition-colors duration-300">
                  <h3 className="text-xl font-bold text-neurops-black mb-3 group-hover:text-gray-600 transition-colors duration-300">
                    {feature.title}
                  </h3>
                  <p className="text-gray-600 leading-relaxed">
                    {feature.description}
                  </p>
                </div>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default About;
